package practise;

public class nn1 {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
	for(int i=0;i<5;i++) {
		for(int j=0;j<5;j++) {
			if(5-i-1<=j)
				System.out.print("*");
			else
				System.out.print(" ");
	
		}
		System.out.println();
	}
		}

}
